import { TodoWrapper } from './components/TodoWrapper';
// App.js
import React from 'react';
import { useDispatch } from './redux/reducer';
import { sortData } from './redux/actions';

function App() {
  const dispatch = useDispatch();

  const handleSort = (sortBy) => {
    dispatch(sortData(sortBy));
  };

  return (
    <div className="App">
      <button onClick={() => handleSort('asc')}>Sort A-Z</button>
      <button onClick={() => handleSort('desc')}>Sort Z-A</button>
      {/* TodoWrapper component here */}
      <TodoWrapper/>
    </div>
  );
}

export default App;
